def _fetch_jwks(
    settings: ClerkSettings,
    *,
    http_get: Optional[Callable[[str], Dict[str, Any]]] = None,
) -> Dict[str, Any]:
    """Fetch Clerk JWKS document with naive in-memory caching."""
    now = time.time()

    cache_entry = _JWKS_CACHE.get(settings.jwks_url)
    if cache_entry and cache_entry["expires_at"] > now:
        return cache_entry["payload"]

    fetcher: Callable[[str], Dict[str, Any]]
    if http_get is not None:
        fetcher = http_get
    else:
        def fetcher(url: str) -> Dict[str, Any]:
            logger.info(f"🔍 Fetching JWKS from: {url}")
            try:
                response = httpx.get(url, timeout=5.0)
                logger.info(f"✅ JWKS response status: {response.status_code}")
                response.raise_for_status()
                return response.json()
            except httpx.ConnectError as e:
                logger.error(f"❌ JWKS connection error: {e}")

