from typing import Optional


def extract_email_from_claims(claims: dict) -> Optional[str]:
    """Extract email from Clerk claims."""
    email = claims.get("email")
    if email:
        return email.lower().strip()
    # Clerk may nest email inside email_addresses array
    email_addresses = claims.get("email_addresses") or []
    for entry in email_addresses:
        if entry.get("id") == claims.get("email_address_id"):
            extracted_email = entry.get("email_address")
            if extracted_email:
                return extracted_email.lower().strip()
    return None


def extract_roles_from_claims(claims: dict) -> list[str]:
    """Extract roles from Clerk claims."""
    metadata = claims.get("public_metadata") or {}
    roles = metadata.get("roles")
    if isinstance(roles, list):
        return [str(role) for role in roles]
    if isinstance(roles, str):
        return [roles]
    return []


def extract_daily_override_from_claims(claims: dict) -> Optional[int]:
    metadata = claims.get("private_metadata") or {}
    override = metadata.get("dailyLimitOverride")
    try:
        return int(override) if override is not None else None
    except (TypeError, ValueError):
        return None


def normalize_roles(roles: list[str], is_admin: bool = False) -> list[str]:
    """Normalize roles list. Add admin if is_admin is True."""
    normalized = set(roles)
    if is_admin:
        normalized.add("admin")
    return sorted(list(normalized))

