        if path.startswith(f"{category}.")
    ]


def get_output_type(field_path: str) -> FieldType:
    definition = get_field_definition(field_path)
    if definition:
        return definition.field_type
    return FieldType.STRING  # Default


def validate_field_type(field_path: str, value: Any) -> bool:
    definition = get_field_definition(field_path)
    if not definition:
        return True  # Unknown field, skip validation

    # Basic type checking
    field_type = definition.field_type

    if field_type == FieldType.STRING and not isinstance(value, str):
        return False
    if field_type == FieldType.INTEGER and not isinstance(value, int):
        return False
    if field_type == FieldType.FLOAT and not isinstance(value, (int, float)):
        return False
    if field_type == FieldType.BOOLEAN and not isinstance(value, bool):
        return False
    if field_type == FieldType.URL and not isinstance(value, str):
        return False
    if field_type == FieldType.ARRAY_STRING and not (isinstance(value, list) and all(isinstance(item, str) for item in value)):
        return False
    if field_type == FieldType.ARRAY_OBJECT and not (isinstance(value, list) and all(isinstance(item, dict) for item in value)):
        return False
    if field_type == FieldType.OBJECT and not isinstance(value, dict):
        return False

    # For Pydantic models, use the model's validation
    if field_type == FieldType.PYDANTIC_MODEL and definition.pydantic_model:
        from prospecting.enrichment_models import validate_field_with_pydantic
        return validate_field_with_pydantic(field_path, value)

    return True

