import httpx
import jwt  # PyJWT
from jwt.exceptions import InvalidTokenError, DecodeError
from jwcrypto import jwk as jwcrypto_jwk

from common.utils.config import (
    get_clerk_audience,
    get_clerk_issuer,
    get_clerk_jwks_url,
    get_lp_clerk_audience,
    get_lp_clerk_issuer,
    get_lp_clerk_jwks_url,
)

logger = logging.getLogger(__name__)


class ClerkTokenError(RuntimeError):
    """Raised when a Clerk token fails validation."""


class ClerkConfigurationError(ClerkTokenError):
    """Raised when mandatory Clerk configuration values are missing."""


@dataclass(frozen=True)
class ClerkSettings:
    jwks_url: str
    issuer: str
    audience: Optional[str] = None
    cache_ttl_seconds: int = 300

    @classmethod
    def from_config(cls) -> "ClerkSettings":
        jwks_url = get_clerk_jwks_url()
        issuer = get_clerk_issuer()
        audience = get_clerk_audience()

        missing = [name for name, value in (
            ("CLERK_JWKS_URL", jwks_url),
            ("CLERK_ISSUER", issuer),
        ) if not value]
        if missing:
            raise ClerkConfigurationError(
                f"Missing Clerk configuration value(s): {', '.join(missing)}"
            )

        return cls(jwks_url=jwks_url, issuer=issuer, audience=audience)


def get_clerk_settings() -> ClerkSettings:
    """Load Clerk token validation settings from config helpers."""
    return ClerkSettings.from_config()


def get_lp_clerk_settings() -> ClerkSettings:
    jwks_url = get_lp_clerk_jwks_url()
    issuer = get_lp_clerk_issuer()
    audience = get_lp_clerk_audience()

