        def fetcher(url: str) -> Dict[str, Any]:
            logger.info(f"🔍 Fetching JWKS from: {url}")
            try:
                response = httpx.get(url, timeout=5.0)
                logger.info(f"✅ JWKS response status: {response.status_code}")
                response.raise_for_status()
                return response.json()
            except httpx.ConnectError as e:
                logger.error(f"❌ JWKS connection error: {e}")
                raise
            except httpx.HTTPStatusError as e:
                logger.error(f"❌ JWKS HTTP error: {e.response.status_code} - {e.response.text}")
                raise
            except Exception as e:
                logger.error(f"❌ JWKS fetch error: {e}")
                raise

    logger.info(f"🔍 Attempting to fetch JWKS from: {settings.jwks_url}")
    payload = fetcher(settings.jwks_url)
    if "keys" not in payload:
        raise ClerkTokenError("JWKS payload missing 'keys' entry")

    _JWKS_CACHE[settings.jwks_url] = {
        "payload": payload,
        "expires_at": now + settings.cache_ttl_seconds,
    }
    return payload


def _select_signing_key(token: str, jwks_payload: Dict[str, Any]) -> Dict[str, Any]:
    """Return the JWK matching the token's `kid` header."""
    # PyJWT has the same get_unverified_header method
    header = jwt.get_unverified_header(token)
    kid = header.get("kid")
    if not kid:
        raise ClerkTokenError("Token header missing 'kid'")

    for candidate in jwks_payload.get("keys", []):
        if candidate.get("kid") == kid:
            return candidate

    raise ClerkTokenError(f"Unable to find matching JWK for kid={kid}")


def verify_clerk_token(
    token: str,
    *,
    settings: Optional[ClerkSettings] = None,
    jwks_fetcher: Optional[Callable[[ClerkSettings], Dict[str, Any]]] = None,
) -> Dict[str, Any]:
    """Validate a Clerk access token and return its claims."""
    resolved_settings = settings or get_clerk_settings()

    if jwks_fetcher is not None:
        jwks_payload = jwks_fetcher(resolved_settings)
    else:
        jwks_payload = _fetch_jwks(resolved_settings)

    key_data = _select_signing_key(token, jwks_payload)
    jwk_obj = jwcrypto_jwk.JWK.from_json(json.dumps(key_data))

