# Django uses request.META for headers (prefixed with HTTP_)
# FastAPI uses request.headers
search_context_header = None

# Try Django-style header access
if hasattr(request, "META"):
    search_context_header = request.META.get("HTTP_X_SEARCH_CONTEXT")
# Try FastAPI-style header access (for backward compatibility)
elif hasattr(request, "headers"):
    search_context_header = request.headers.get("X-Search-Context")

if not search_context_header:
    return None

try:
    return json.loads(search_context_header)
except json.JSONDecodeError:
    from common.utils.logging_config import get_logger

    logger = get_logger(__name__)
    logger.warning(f"Invalid search context header: {search_context_header}")
    return None
